package in.handyman.raven.lib;

import com.fasterxml.jackson.databind.ObjectMapper;
import in.handyman.raven.lambda.action.ActionExecution;
import in.handyman.raven.lambda.action.IActionExecution;
import in.handyman.raven.lambda.doa.audit.ActionExecutionAudit;
import in.handyman.raven.lib.model.FtpsConnector;
import org.apache.commons.net.PrintCommandListener;
import org.apache.commons.net.ftp.*;
import org.apache.commons.net.util.TrustManagerUtils;
import org.slf4j.Logger;
import org.slf4j.Marker;
import org.slf4j.MarkerFactory;

import java.io.*;

/**
 * Auto Generated By Raven
 */
@ActionExecution(
        actionName = "FtpsConnector"
)
public class FtpsConnectorAction implements IActionExecution {
    private final ActionExecutionAudit action;

    private final Logger log;

    private final FtpsConnector ftpsConnector;

    private final Marker aMarker;

    private final ObjectMapper mapper = new ObjectMapper();

    public FtpsConnectorAction(final ActionExecutionAudit action, final Logger log,
                               final Object ftpsConnector) {
        this.ftpsConnector = (FtpsConnector) ftpsConnector;
        this.action = action;
        this.log = log;
        this.aMarker = MarkerFactory.getMarker(" FtpsConnector:" + this.ftpsConnector.getName());
    }

    @Override
    public void execute() throws Exception {
        final String remoteHost = ftpsConnector.getHost();
        final String userName = ftpsConnector.getUserName();
        final String password = ftpsConnector.getPassword();
        final int remotePort = Integer.parseInt(ftpsConnector.getPort());
        final int sessionTimeout = Integer.parseInt(ftpsConnector.getSessionTimeOut());
        final String destDir = ftpsConnector.getDestDir();
        final String remoteFile = ftpsConnector.getSourceFile();
        log.info(aMarker, "Got the sftp details for the host {} and user {}", remoteHost, userName);
        FTPSClient ftpClient = new FTPSClient();
        try {
            ftpClient.setEndpointCheckingEnabled(false);
            ftpClient.addProtocolCommandListener(new PrintCommandListener(new PrintWriter(System.out)));
            int reply;
            ftpClient.setConnectTimeout(sessionTimeout);
            ftpClient.connect(remoteHost, remotePort);
            log.info(aMarker, "FTP URL is: {} ", ftpClient.getDefaultPort());
            reply = ftpClient.getReplyCode();
            if (!FTPReply.isPositiveCompletion(reply)) {
                ftpClient.disconnect();
                log.error("Exception in connecting to FTP Server");
            }
            ftpClient.login(userName, password);
            ftpClient.setFileType(FTP.BINARY_FILE_TYPE);
            ftpClient.enterLocalPassiveMode();
            ftpClient.setUseEPSVwithIPv4(true);
            ftpClient.execPBSZ(0);
            ftpClient.execPROT("P");
            System.setProperty("https.protocols", "TLSv1,TLSv1.1,TLSv1.2");
            ftpClient.setTrustManager(TrustManagerUtils.getAcceptAllTrustManager());
            ftpClient.setFileType(FTP.BINARY_FILE_TYPE);
            log.info(aMarker, "Remote system is  {}", (Object) ftpClient.getEnabledCipherSuites());
            log.info(aMarker, "SSL: {}", ftpClient.getEnableSessionCreation());
            log.info(aMarker, "Remote system is {} ", ftpClient.getSystemType());
            String workingDirectory = ftpClient.printWorkingDirectory();
            log.info(aMarker, "Current directory is {}", workingDirectory);
            ftpDownloadDirectory(ftpClient, remoteFile, "", destDir);
            String name = "ftps-file-download-connector-response";
            action.getContext().put(name, mapper.readTree(destDir).toString());
            uploadDirectory(ftpClient, destDir, remoteFile, "");

        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (ftpClient.isConnected()) {
                ftpClient.logout();
                ftpClient.disconnect();
            }
        }
    }

    public void ftpDownloadDirectory(FTPClient ftpClient, String parentDir, String currentDir, String saveDir) throws IOException {
        String dirToList = parentDir;
        if (!currentDir.equals("")) dirToList += File.separator + currentDir;
        FTPFile[] subFiles = ftpClient.listFiles(dirToList);
        fileIteration(ftpClient, parentDir, currentDir, saveDir, dirToList, subFiles);
    }

    private void fileIteration(FTPClient ftpClient, String parentDir, String currentDir, String saveDir, String dirToList, FTPFile[] subFiles) throws IOException {
        if (subFiles != null && subFiles.length > 0) {
            subFilesProcessing(ftpClient, parentDir, currentDir, saveDir, dirToList, subFiles);
        }
    }

    private void subFilesProcessing(FTPClient ftpClient, String parentDir, String currentDir, String saveDir, String dirToList, FTPFile[] subFiles) throws IOException {
        for (FTPFile aFile : subFiles) {
            String currentFileName = aFile.getName();
            if (currentFileName.equals(".") || currentFileName.equals("..")) continue;
            String filePath = parentDir + File.separator + currentDir + File.separator + currentFileName;
            if (currentDir.equals("")) filePath = parentDir + File.separator + currentFileName;
            String newDirPath = saveDir + parentDir + File.separator + currentDir + File.separator + currentFileName;
            if (currentDir.equals("")) newDirPath = saveDir + parentDir + File.separator + currentFileName;
            if (aFile.isDirectory())
                createDirectoryForSubFilesAndIterate(ftpClient, saveDir, dirToList, currentFileName, newDirPath);
            else downloadSingleFile(ftpClient, filePath, newDirPath);
        }
    }

    private void createDirectoryForSubFilesAndIterate(FTPClient ftpClient, String saveDir, String dirToList, String currentFileName, String newDirPath) throws IOException {
        File newDir = new File(newDirPath);
        boolean created = newDir.mkdirs();
        if (created) log.info(aMarker, "Created the directory: {} ", newDirPath);
        else log.info(aMarker, "Couldn't create the directory: {} ", newDirPath);
        ftpDownloadDirectory(ftpClient, dirToList, currentFileName, saveDir);
    }

    public void downloadSingleFile(FTPClient ftpClient, String remoteFilePath, String savePath) throws IOException {
        File downloadFile = new File(savePath);
        File parentDir = downloadFile.getParentFile();
        if (!parentDir.exists()) {
            boolean createDirSuccessful = parentDir.mkdir();
            if (!createDirSuccessful) log.error("Problem in creating the directory {} ", parentDir);
        }
        try (OutputStream outputStream = new BufferedOutputStream(new FileOutputStream(downloadFile))) {
            ftpClient.setFileType(FTP.BINARY_FILE_TYPE);
            log.info(aMarker, "Download file {} ", savePath);
            boolean success = ftpClient.retrieveFile(remoteFilePath, outputStream);
            if (success) log.info(aMarker, "Download the file {} ", remoteFilePath);
            else log.info(aMarker, "Couldn't download the file: {} ", remoteFilePath);
        }
    }

    public void uploadDirectory(FTPClient ftpClient, String remoteDirPath, String localParentDir, String remoteParentDir) throws IOException {
        log.info(aMarker, "Listing directory {} ", localParentDir);
        File localDir = new File(localParentDir);
        File[] subFiles = localDir.listFiles();
        if (subFiles != null && subFiles.length > 0) {
            for (File item : subFiles) {
                String remoteFilePath = remoteDirPath + File.separator + remoteParentDir + File.separator + item.getName();
                if (remoteParentDir.equals("")) {
                    remoteFilePath = remoteDirPath + File.separator + item.getName();
                }
                if (item.isFile()) {
                    singleFileUploadProcessing(ftpClient, item, remoteFilePath);
                } else {
                    directoryUploadProcessing(ftpClient, remoteDirPath, remoteParentDir, item, remoteFilePath);
                }
            }
        }
    }

    private void directoryUploadProcessing(FTPClient ftpClient, String remoteDirPath, String remoteParentDir, File item, String remoteFilePath) throws IOException {
        String localParentDir;
        boolean created = ftpClient.makeDirectory(remoteFilePath);
        if (created) {
            log.info(aMarker, "Created the directory: {} ", remoteFilePath);
        } else {
            log.info(aMarker, "Couldn't create the directory: {} ", remoteFilePath);
        }
        // upload the subdirectory
        String parent = remoteParentDir + File.separator + item.getName();
        if (remoteParentDir.equals("")) {
            parent = item.getName();
        }
        localParentDir = item.getAbsolutePath();
        uploadDirectory(ftpClient, remoteDirPath, localParentDir, parent);
    }

    private void singleFileUploadProcessing(FTPClient ftpClient, File item, String remoteFilePath) throws IOException {
        // upload the file
        String localFilePath = item.getAbsolutePath();
        log.info(aMarker, "About to upload the file: {} ", localFilePath);
        uploadSingleFile(ftpClient, localFilePath, remoteFilePath);
    }

    public void uploadSingleFile(FTPClient ftpClient, String localFilePath, String remoteFilePath) throws IOException {
        File localFile = new File(localFilePath);
        try (InputStream inputStream = new FileInputStream(localFile)) {
            ftpClient.setFileType(FTP.BINARY_FILE_TYPE);
            boolean uploaded = ftpClient.storeFile(remoteFilePath, inputStream);
            if (uploaded) {
                log.info(aMarker, "Uploaded a file to: {}", remoteFilePath);
            } else {
                log.info(aMarker, "COULD NOT upload the file: {} ", localFilePath);
            }
        }
    }

    @Override
    public boolean executeIf() throws Exception {
        return ftpsConnector.getCondition();
    }
}
